mapper.select:
SELECT B.* FROM ( SELECT @ROWNUM := @ROWNUM+1 AS GOODS_SEQ , GDS.* FROM ( SELECT G.* , GI.STORED_FILE_NAME , GI.IMG_URL FROM GOODS G LEFT JOIN GOODS_IMG_FILE GI ON G.GDS_ID = GI.GDS_ID WHERE G.DEL_YN = 'N'
AND GDS_NAME LIKE CONCAT('%',#{keyword},'%') AND CAT_CODE LIKE CONCAT('%',#{searchType},'%')
) GDS ) B LIMIT #{startList}, #{listSize}

SELECT GDS.* FROM ( SELECT G.* , GI.STORED_FILE_NAME , GI.IMG_URL FROM GOODS G LEFT JOIN GOODS_IMG_FILE GI ON G.GDS_ID = GI.GDS_ID WHERE G.DEL_YN = 'N'
AND G.CAT_CODE = #{searchType}
) GDS LIMIT #{startNum}, #{endNum}

SELECT COUNT(*) FROM ( SELECT G.* , GI.STORED_FILE_NAME FROM GOODS G LEFT JOIN GOODS_IMG_FILE GI ON G.GDS_ID = GI.GDS_ID WHERE G.DEL_YN = 'N'
AND GDS_NAME LIKE CONCAT('%',#{keyword},'%') AND CAT_CODE LIKE CONCAT('%',#{searchType},'%')
) GDS

mapper.insert:
INSERT
INTO USER(
           GDS_ID
         , GDS_NAME
         , GDS_PRICE
         , CAT_CODE
         , GDS_URL
         , DEL_YN
) VALUES(
          #{gdsId}
        , #{gdsName}
        , #{gdsPrice}
        , #{catCode}
        , #{gdsURL}
        , 'N'
        )

mapper.update:
UPDATE GOODS
SET GDS_NAME = #{gdsName}
  , GDS_PRICE = #{gdsPrice}
  , CAT_CODE = #{catCode}
  , GDS_URL = NULL
WHERE GDS_ID = #{gdsId}

UPDATE GOODS
SET GDS_NAME = #{gdsName}
  , GDS_PRICE = #{gdsPrice}
  , CAT_CODE = #{catCode}
  , GDS_URL = #{gdsURL}
WHERE GDS_ID = #{gdsId}

mapper.insert:
INSERT
INTO USER(
           IMGFILE_ID
         , GDS_ID
         , ORIGIN_FILE_NAME
         , STORED_FILE_NAME
         , FILE_SIZE
         , CREATE_DATE
         , DEL_YN
) VALUES(
          #{imgFileId}
        , #{gdsId}
        , #{originFileName}
        , #{storedFileName}
        , #{fileSize}
        , NOW()
        , 'N'
        )

mapper.update:
UPDATE GOODS
SET DEL_YN = 'Y'
WHERE GDS_ID = #{gdsId}
