mapper.select:
SELECT O.ORDER_ID , CASE WHEN COUNT(OD.GDS_ID) != 1 THEN CONCAT(CONCAT(CONCAT(G.GDS_NAME,' 외 '),COUNT(OD.GDS_ID)-1),'개') ELSE G.GDS_NAME END AS ORDER_NAME, D.IMG_URL , D.STORED_FILE_NAME , O.TOTAL_PRICE , O.ORDER_STATUS_CODE , CASE WHEN O.ORDER_STATUS_CODE = 'A001' THEN '대기' WHEN O.ORDER_STATUS_CODE = 'A002' THEN '승인' WHEN O.ORDER_STATUS_CODE = 'A003' THEN '반려' ELSE '주문실패' END AS ORDER_STATUS , O.USER_ID , U.USER_NAME FROM `ORDER` O LEFT JOIN ORDER_DETAIL OD ON O.ORDER_ID = OD.ORDER_ID LEFT JOIN GOODS G ON OD.GDS_ID = G.GDS_ID LEFT JOIN GOODS_IMG_FILE D ON OD.GDS_ID = D.GDS_ID LEFT JOIN USER U ON U.USER_ID = O.USER_ID WHERE O.DEL_YN = 'N'
AND O.USER_ID = #{searchType}
AND O.ORDER_STATUS_CODE = #{searchType2}
GROUP BY ORDER_ID

SELECT COUNT(*) AS ORDER_CNT
FROM `ORDER`
WHERE 1=1

    AND USER_ID = #{searchType}


    AND ORDER_STATUS_CODE = #{searchType2}

mapper.update:
UPDATE CART
SET    QTY = #{qty}
     , LAST_CHG_DT = NOW()
WHERE  CART_ID = #{cartId}

UPDATE `ORDER`
SET ORDER_STATUS_CODE = #{statusCode}
WHERE ORDER_ID IN

    #{item}

UPDATE CRTR_MNG
SET ORDER_AMT = #{orderAmt}
AND USER_AMT = #{userAmt}
WHERE USER_ID = #{userId}
  AND DEL_YN = 'N'
