mapper.insert:
INSERT
  INTO USER(
       USER_ID
     , USER_NAME
     , DEPT_CODE
     , AGE
     ) VALUES(
       #{userId}
     , #{userName}
     , #{deptCode}
     , #{age}
     )

mapper.update:
UPDATE USER
   SET USER_NAME = #{userName}
     , DEPT_CODE = #{deptCode}
     , AGE = #{age}
 WHERE USER_ID = #{userId}
